Page({
    clickList(e) {
        console.log(e);
        if (e.detail === 0) {
            swan.showToast({
                title: '你点击了办理须知',
                icon: 'none'
            });
        }
        else if (e.detail === 1) {
            swan.showToast({
                title: '你点击了常见问题',
                icon: 'none'
            });
        }
        else if (e.detail === 2) {
            swan.showToast({
                title: '你点击了办理机构及流程',
                icon: 'none'
            });
        }
    },
    clickBtn() {
        swan.showToast({
            title: '你点击了btn按钮',
            icon: 'none'
        });
    }
});
