/**
 * @file index.js
 * @author swan
 * @date 2020-02-21
 */

Page({
    data: {
        imageList: [],
        showImage: [],
        borderList: [],
        classList: [],
        lineList: [],
        sizeImage: [],
        urlemptyImage: []
    },

    urlempty({detail}) {
        // 可以在这里做自己的上传图片
        this.setData('urlemptyImage', detail);
    },

    deleteUrlempty(e) {
        this.setData('urlemptyImage', e.detail.path);
    },

    previewfail({detail}) {
        swan.showToast({
            title: '预览图片失败',
            icon: 'none'
        });
    },

    tap({res}) {
        if (res.code === 0) {
            this.setData('imageList', res.data);
        }
        else {
            swan.showToast({
                title: '上传失败，请稍后重试',
                icon: 'none'
            });
        }
    },

    clickDelete(e) {
        this.setData('imageList', e.detail.path);
    },

    tapImgSize({res}) {
        if (res.code === 0) {
            this.setData('sizeImage', res.data);
        }
    },

    sizeDelete(e) {
        this.setData('sizeImage', e.detail.path);
    },

    taps({res}) {
        if (res.code === 0) {
            this.setData('showImage', res.data);
        }
        else {
            swan.showToast({
                title: '上传失败，请稍后重试',
                icon: 'none'
            });
        }
    },

    clickDeletes(e) {
        this.setData('showImage', e.detail.path);
    },

    borderTap({res}) {
        if (res.code === 0) {
            this.setData('borderList', res.data);
        }
        else {
            swan.showToast({
                title: '上传失败，请稍后重试',
                icon: 'none'
            });
        }
    },

    borderDelete(e) {
        this.setData('borderList', e.detail.path);
    },

    classUp({res}) {
        if (res.code === 0) {
            this.setData('classList', res.data);
        }
        else {
            swan.showToast({
                title: '上传失败，请稍后重试',
                icon: 'none'
            });
        }
    },

    classDelete(e) {
        this.setData('classList', e.detail.path);
    },

    lineUp({res}) {
        if (res.code === 0) {
            this.setData('lineList', res.data);
        }
        else {
            swan.showToast({
                title: '上传失败，请稍后重试',
                icon: 'none'
            });
        }
    },

    lineDelete(e) {
        this.setData('lineList', e.detail.path);
    }
});
