/**
 * @file index.js
 * @author swan
 * @date 2020-03-06
 */

Page({
    data: {
        showPanel: false,
        voiceCont: '',
        showShortPanel: false,
        voiceShortCont: '',
        showAutoPanel: false,
        showAutoCont: '',
        showManualPanel: false,
        showManualCont: '',
        showTextPanel: false,
        showTextCont: '',
        showColorPanel: false,
        showColorCont: ''
    },
    openVoicePanel() {
        this.setData({
            showPanel: true
        });
    },
    openShortVoicePanel() {
        this.setData({
            showShortPanel: true
        });
    },
    openAutoVoicePanel() {
        this.setData({
            showAutoPanel: true
        });
    },
    openManualVoicePanel() {
        this.setData({
            showManualPanel: true
        });
    },
    openTextVoicePanel() {
        this.setData({
            showTextPanel: true
        });
    },
    openColorVoicePanel() {
        this.setData({
            showColorPanel: true
        });
    },
    cancelEnd() {
        this.setData({
            showPanel: false
        });
        this.setData({
            showShortPanel: false
        });
    },
    cancelShortEnd() {
        this.setData({
            showShortPanel: false
        });
    },
    cancelAutoEnd() {
        this.setData({
            showAutoPanel: false
        });
    },
    cancelManualEnd() {
        this.setData({
            showManualPanel: false
        });
    },
    cancelTextEnd() {
        this.setData({
            showTextPanel: false
        });
    },
    cancelColorEnd() {
        this.setData({
            showColorPanel: false
        });
    },
    getCont(e) {
        this.setData({
            voiceCont: e.content
        });
    },
    getShortCont(e) {
        this.setData({
            voiceShortCont: e.content
        });
    },
    getAutoCont(e) {
        this.setData({
            voiceAutoCont: e.content
        });
    },
    getManualCont(e) {
        this.setData({
            voiceManualCont: e.content
        });
    },
    getTextCont(e) {
        this.setData({
            voiceTextCont: e.content
        });
    },
    getColorCont(e) {
        this.setData({
            voiceColorCont: e.content
        });
    }
});
