"use strict";

/**
 * @file 阅读并接受组件
 * @author swan
 */
Component({
  externalClasses: ['gov-container', 'gov-agree-desc'],
  properties: {
    checked: {
      type: Boolean,
      value: false
    },
    iconColor: {
      type: String,
      value: '#2B99FF'
    }
  },
  data: {
    value: '我已接受并阅读'
  },
  methods: {
    checkdChange: function checkdChange(e) {
      this.setData({
        checked: !this.data.checked
      });
      this.triggerEvent('change', {
        value: this.data.checked
      });
    }
  }
});