"use strict";

/**
 * @file 按钮组组件
 * @author swan
 */
Component({
  externalClasses: ['gov-button-group'],
  properties: {
    buttonGroup: {
      type: Array,
      value: []
    },
    buttonPosition: {
      type: String,
      value: ''
    },
    buttonArrange: {
      type: String,
      value: ''
    }
  },
  methods: {
    buttonHandle: function buttonHandle(e) {
      var index = e.currentTarget.dataset.index;
      this.triggerEvent('buttonHandle', {
        index: index
      });
    }
  }
});