demoCode.js:
data: {
        smsData: {
            type: 'text',
            maxLength: 4,
            focus: false,
            placeholderContent: '请输入验证码',
            defaultValue: '',
            iptName: 'smsData',
            iptErrorFlag: true,
            iptErrorColor: 'blue'
        }
    },
    iptKeyInput(e) {
        console.log('输入组件发生了keyinput事件，携带数据为：', e.detail);
    },
    iptBlur(e) {
        console.log('输入组件发生了blur事件，携带数据为：', e.detail);
    },
    iptFocus(e) {
        console.log('输入组件发生了focus事件，携带数据为：', e.detail);
    },
    iptConfirm() {
        console.log('输入组件发生了confirm事件');
    },
    bindSmsSend() {
        console.log('短信已发送');
    }
