"use strict";

var _index = require("../../common/utils/index.js");

/**
 * @file 状态提示组件
 * @author swan
 */
Component({
  externalClasses: ['gov-spin-loading', 'gov-spin-wrap', 'gov-spin-text'],
  properties: {
    status: {
      type: Number,
      value: 1
    },
    textConfig: {
      type: Array,
      value: ['点击加载更多', '正在加载...', '已经到底啦', '加载失败 点击重新加载']
    },
    secureBottom: {
      type: Boolean,
      value: true
    },
    imgSrc: {
      type: String,
      value: './imgs/loading.png'
    }
  },
  data: {
    isFullScreen: _index.isFullScreen
  },
  methods: {
    onTap: function onTap() {
      if (this.properties.status === 1 || this.properties.status === 2) {
        return;
      }

      this.triggerEvent('spintap');
    }
  }
});