"use strict";

/**
 * @file 小程序通用组件-table-item
 * @author swan
 */

/* global Component, swan */
Component({
  externalClasses: ['gov-content-item', 'gov-table-item-title', 'gov-table-item-desc', 'gov-content-item-line'],
  properties: {
    // 表格的数据
    main: {
      type: Object,
      value: {}
    },
    // 是否有下边框线
    bottomBorder: {
      type: Boolean,
      value: true
    }
  },
  methods: {
    tipsTap: function tipsTap(e) {
      swan.showModal({
        content: e.currentTarget.dataset.item.content,
        showCancel: false,
        confirmText: '我知道了',
        cancelColor: '#2b99ff'
      });
    }
  }
});