Page({
    data: {
        inputGroup: [
            {
                iptItemType: 'short',
                iptTitle: '普通短标',
                iptName: 'username',
                placeholderContent: '请输入',
                iptValue: '不可修改disable',
                type: 'text',
                passWord: false,
                maxLength: 140,
                focus: false,
                disabled: true,
                confirmType: 'go',
                iptErrorInfo: {
                    errFlag: false,
                    errContent: '密码输错了，请重试！'
                },
                iptNoticInfo: {
                    noticFlag: true,
                    noticType: 'modal',
                    noticToast: {
                        toastTitle: 'toast的一些个文案～',
                        toastIcon: 'none'
                    },
                    noticModal: {
                        modalTitle: '标题',
                        modalContent: 'modal的一些个文案！'
                    }
                }
            },
            {
                iptItemType: 'short',
                iptTitle: '四字标题'
            },
            {
                iptTitle: '六字标题六字'
            },
            {
                iptItemType: 'two-row',
                iptTitle: '两行标题两行标题两行标题'
            },
            {
                iptItemType: 'short',
                iptTitle: '身份证号',
                type: 'idcard',
                placeholderContent: '请输入身份证号'
            },
            {
                iptItemType: 'short',
                iptTitle: '手机号',
                type: 'number',
                placeholderContent: '请输入手机号'
            },
            {
                iptItemType: 'short',
                iptTitle: '密码',
                placeholderContent: '请输入密码',
                password: true
            },
            {
                iptTitle: '身份证号六位',
                type: 'idcard',
                placeholderContent: '请输入身份证号'
            },
            {
                iptTitle: '手机号六位位',
                type: 'number',
                placeholderContent: '请输入手机号'
            },
            {
                iptItemType: 'short',
                iptTitle: '四字标题超出四字标题超出',
                placeholderContent: '四字标题超出四字标题超出'
            },
            {
                iptTitle: '六字标题超出六字标题超出',
                placeholderContent: '六字标题超出六字标题超出'
            },
            {
                iptItemType: 'two-row',
                iptTitle: '两行标题超出两行标题超出',
                placeholderContent: '暂不支持两行超级截断隐藏，建议在12字内'
            },
            {
                placeholderContent: '提示文案'
            }
        ],
        error: new Array(12).fill({errFlag: false, errContent: '请输入xxxx'})
    },
    iptKeyInput(e) {// 捕获input子组件的keyinput
        console.log('输入组件发生了keyinput事件，携带数据为：', e.detail);
    },
    iptBlur(e) {// 捕获input子组件的blur事件
        console.log('输入组件发生了blur事件，携带数据为：', e.detail);
        let index = e.currentTarget.dataset.index;
        const detail = e.detail;

        if (!detail.iptvalue) {
            this.setData('error[' + index + ']', {
                errFlag: true,
                errContent: '请输入' + detail.iptname
            });
        }

    },
    iptFocus(e) {// 捕获input子组件的focus事件
        console.log('输入组件发生了focus事件，携带数据为：', e.detail);
    },
    iptConfirm() {// 捕获input子组件的confirm事件
        console.log('输入组件发生了confirm事件');
    },
    formSubmit(e) {
        console.log('form发生了submit事件，携带数据为：', e.detail.value);
    },
    formReset() {
        console.log('form表单reset');
    },
    handleClean() {
        console.log('cleanout');
    }
});
