Page({
    data: {
        activeName: 'tab2',

        iconActiveName: 'postal-m'
    },

    change({detail}) {
        this.setData({
            activeName: detail
        });
    },

    iconChange({detail}) {
        this.setData('iconActiveName', detail);
    }
});
