demoCode.css:
.gov-button-group {
    position: absolute !important;
}

.gov-bottom {
    position: absolute !important;
    background-color: transparent !important;
    padding: 0 !important;
}

.gov-wrap {
    height: 100%;
}

.gov-hor {
    height: 100px;
    margin-bottom: 20px;
    box-sizing: border-box;
}

.gov-ver {
    height: 170px;
    margin-bottom: 20px;
    box-sizing: border-box;
}

.gov-title {
    width: 100%;
    height: 30px;
}

.gov-hor .gov-content {
    position: relative;
    height: 70px;
    background-color: rgb(243, 243, 242);
}

.gov-ver .gov-content {
    position: relative;
    height: 150px;
    background-color: rgb(243, 243, 242);;
}
