/**
 * 组件选择 * 避免s-if出问题
 *
 * @param {Object} context this
 * @param {string} selector 选择操作符
 */
export const selComponent: (context: Object, selector: string) => Promise<void>;

/**
 * debounce 支持取消的防抖
 * @param {Function} func 执行的函数
 * @param {number=} wait 等待时间
 * @param {Object=} options 附加参数：初始执行、最大时间
 */
export const debounce: (func: Function, wait?: number, options?: any) => Function;

/**
 * 简易版 promiseDebounce
 *
 * @param {Function} fn 执行函数
 * @param {number} delay 间隔
 */
export const promiseDebounce: (fn: Function, delay: number) => Promise<void>;

/**
 * 线性累加
 *
 * @param {Function} exector 执行函数 * 该函数参数包含 当前高度、终止高度、回调
 * @param {number=} start 初始大小
 * @param {number=} ms 时间
 * @param {number=} fps 帧率
 */
export const linearAccu: (exector: Function, start?: number, end?: number, ms?: number, fps?: number) => Promise<void>;

/**
 * setData 方便 await
 * @param {Object} context this
 * @param {Object} obj 要设置的值
 * @param {Promise}
 */
export const syncSetData: (context: any, obj: any) => Promise<void>;

/**
 * 平台信息
 */
export const systemInfo: any;

/**
 * 判断机型
 * @param {string=} mobile 手机型号
 */
export const checkModel: (mobile?: string) => boolean;

/**
 * 是否iso
 */
export const isIos: boolean;
