"use strict";

var _index = require("../../common/utils/index");

/**
 * @file 横向滚动列表
 * @author v_zhangxin19(v_zhangxin19@baidu.com)
 */
Component({
  externalClasses: ['gov-swiper-table', 'gov-table-left', 'gov-table-right', 'gov-table-last', 'gov-table-right-middle', 'gov-mask-right-cover'],
  properties: {
    // 渲染数据
    listDetail: {
      type: Object,
      value: {}
    },
    // 最后一行的背景颜色
    lastTableBg: {
      type: String,
      value: '#fff'
    },
    // 最后一行的字体颜色
    lastTableColor: {
      type: String,
      value: '#666'
    },
    // 最后一行是否特殊
    lastRowDif: {
      type: Boolean,
      value: false
    },
    // 右侧宽度自定义
    widthFlag: {
      type: Array,
      value: []
    },
    // 右侧高度自定义
    heightFlag: {
      type: Array,
      value: []
    },
    // 右侧滚动区域的宽度
    scrollAreaWidth: {
      type: String,
      value: '300px'
    },
    // 超出两行截断时提示icon的颜色
    iconColor: {
      type: String,
      value: '#2b99ff'
    }
  },
  data: {
    maskerFlag: false,
    isIos: _index.isIos
  },
  lower: function lower() {
    this.setData({
      maskerFlag: false
    });
  },
  upper: function upper() {
    this.setData({
      maskerFlag: true
    });
  },
  showModal: function showModal(e) {
    swan.showModal({
      content: e.currentTarget.dataset.info,
      confirmText: '我知道了',
      showCancel: false
    });
  }
});