import {isIos} from '../../common/utils/index';

Component({
    externalClasses: [
        'gov-list-item',
        'gov-label',
        'gov-label-desc',
        'gov-content',
        'gov-content-desc'
    ],

    properties: {
        // 内容可复制
        selectable: {
            type: Boolean,
            value: true
        },

        // 左侧文字
        label: {
            type: String,
            value: ''
        },

        // 左侧文字宽度
        labelWidth: {
            type: String,
            value: '4em'
        },

        // 左侧文字描述
        labelDesc: {
            type: String,
            value: ''
        },

        // 右侧文字
        content: {
            type: String,
            value: ''
        },

        // 右侧文字描述
        contentDesc: {
            type: String,
            value: ''
        },

        // 右箭头
        arrow: {
            type: Boolean,
            value: false
        },

        // 底边线
        border: {
            type: Boolean,
            value: false
        },

        // 点击态
        clickable: {
            type: Boolean,
            value: false
        }
    },

    data: {
        isIos
    }
});
