/* global getCurrentPages */
Component({
  externalClasses: ['gov-navigation-container', 'gov-navigation-fixed', 'gov-back-icon', 'gov-title'],
  properties: {
    // 标题
    title: {
      type: String,
      value: ''
    },
    // 显示底部border
    border: {
      type: Boolean,
      value: true
    },
    // 返回icon为主页
    homeIcon: {
      type: Boolean,
      value: getCurrentPages().length === 1
    }
  },
  data: {
    // 状态栏高度
    statusHeight: _index.systemInfo.statusBarHeight,
    // navigation 高度
    navHeight: _index.systemInfo.navigationBarHeight,
    isIos: _index.isIos
  },
  methods: {
    backHdl: function backHdl() {
      var _this = this;

      (0, _index.preventRepeat)(function () {
        return _this.triggerEvent('back', {
          homeIcon: _this.data.homeIcon
        });
      }, 120);
    }
  }
});