/**
 * @file 步骤条
 * @author swan
 */
/* global Page, swan, getApp */
import {COLOR_BLUE} from '../../common/style/color';

Component({ // eslint-disable-line
    externalClasses: [
        'gov-steps',
        'gov-steps-flex',
        'gov-steps-block',
        'gov-step',
        'gov-step-head',
        'gov-step-main'
    ],
    properties: {
        // 定义了name属性，可以在使用组件时，由外部传入。此变量可以直接在组件模板中使用
        active: {
            type: Number,
            value: 1,
            observe() {
                this.scrollIntoViewStr();
            }
        },
        color: {
            type: String,
            value: '#ccc'
        },
        activeColor: {
            type: String,
            value: COLOR_BLUE
        },
        steps: {
            type: Array,
            value: []
        },
        lineColor: {
            type: String,
            value: '#f5f5f5'
        },
        lineActiveColor: {
            type: String,
            value: '#f5f5f5'
        },
        lineStyle: {
            type: String,
            value: 'dashed'
        },
        lineActiveStyle: {
            type: String,
            value: 'solid'
        },
        lineWidth: {
            type: Number,
            value: 3.62
        },
        titleColor: {
            type: String,
            value: '#999'
        },
        titleActiveColor: {
            type: String,
            value: '#000'
        },
        descColor: {
            type: String,
            value: '#999'
        },
        descActiveColor: {
            type: String,
            value: '#999'
        },
        space: {
            type: Number,
            value: 162
        }
    },
    data: {
        scrollIntoView: '',
        colorBlue: COLOR_BLUE,
        stepName: ['one', 'two', 'three', 'four', 'five', 'six', 'seven', 'eight']
    },
    methods: {
        scrollIntoViewStr() {
            let active = this.data.active;

            this.setData({
                scrollIntoView: `id_${active - 1}`
            });
        }
    },
    created() {
        this.scrollIntoViewStr();
    }
});
