"use strict";

/**
 * @file index.js
 * @author LipengJia <jialipeng@baidu.com>
 * @date 2020-01-03
 */
Component({
  properties: {
    name: {
      type: String,
      value: ''
    }
  },
  data: {
    active: false,
    width: 'unset',
    inited: false
  },
  attached: function attached() {
    this.dispatch('itemUpdate', this.data.name);
    this.setData('inited', true);
  },
  methods: {
    setActive: function setActive(state) {
      this.setData({
        active: state
      });
    },
    setWidth: function setWidth(width) {
      this.setData({
        width: width
      });
    },
    tabHdl: function tabHdl() {
      this.dispatch('itemTap', this.data.name);
    }
  }
});