Component({
    properties: {
        name: {
            type: String,
            value: ''
        }
    },

    data: {
        active: false,
        width: 'unset',
        inited: false
    },

    attached() {
        this.dispatch('itemUpdate', this.data.name);
        this.setData('inited', true);
    },

    methods: {
        setActive(state) {
            this.setData({
                active: state
            });
        },

        setWidth(width) {
            this.setData({
                width
            });
        },

        tabHdl() {
            this.dispatch('itemTap', this.data.name);
        }
    }
});
