demoCode.js:
Page({
    data: {
        showManualPanel: false,
        showManualCont: '',
        showTextPanel: false,
        showTextCont: '',
        showColorPanel: false,
        showColorCont: ''
    },
    openManualVoicePanel() {
        this.setData({
            showManualPanel: true
        });
    },
    openTextVoicePanel() {
        this.setData({
            showTextPanel: true
        });
    },
    openColorVoicePanel() {
        this.setData({
            showColorPanel: true
        });
    },
    cancelManualEnd() {
        this.setData({
            showManualPanel: false
        });
    },
    cancelTextEnd() {
        this.setData({
            showTextPanel: false
        });
    },
    cancelColorEnd() {
        this.setData({
            showColorPanel: false
        });
    },
    getManualCont(e) {
        this.setData({
            voiceManualCont: e.content
        });
    },
    getTextCont(e) {
        this.setData({
            voiceTextCont: e.content
        });
    },
    getColorCont(e) {
        this.setData({
            voiceColorCont: e.content
        });
    }
});

